import React, { Component } from 'react'
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom"
import Data from './movies.json'


export default class Movies extends Component {


    constructor(){
        super();

        state={
          search: '',
          cat: ''
        };
      }

      searchSpace = (event) => {
        let keyword = event.target.value;
        this.setState({search:keyword})
      }

      catSearch = (event) => {
        let keyword = event.target.value;
        this.setState({cat:keyword})
      }

    render() {
        return (
            <div>
                <Router>
                  <div>
                    <ul>
                      <li>
                        <Link to="/upload">Upload</Link>
                      </li>
                      <li>
                      <Link
                        to={{
                          pathname: "/display",
                          state: { search: '' }
                        }}
                      />
                      </li>
                    </ul>

                    <Switch>
                      <Route exact path="/upload">
                        <Upload />
                        </Route>
                        <Route path="/display">
                        <Display />
                        </Route>
                    </Switch>
                  </div>
                </Router>
            </div>
        )
    }
}

function Display() {

  searchSpace = (event) => {
    let keyword = event.target.value;
    this.setState({search:keyword})
  }

  catSearch = (event) => {
    let keyword = event.target.value;
    this.setState({cat:keyword})
  }

  return (
    <div>
      <input type="text" placeholder="Enter name to be searched" onChange={(e)=>this.searchSpace(e)} />
      <input type="text" placeholder="Enter category to be searched" onChange={(e)=>this.catSearch(e)} />

      {Data.map((item) => {

        if (item.name.toLowerCase().includes(this.state.search.toLowerCase()) || item.descr.toLowerCase().includes(this.state.search.toLowerCase())) {
        return <div>
        <img src= {item.thumbnail} />
        <h3>{item.name}</h3>
        <p> {item.descr} </p></div> 
        }
        })}
    </div>
  );
}

function Upload() {
  return (
    <div>
      <h2>Test</h2>
    </div>
  );
}
